import './App.css';
import { useSession, useSupabaseClient, useSessionContext } from '@supabase/auth-helpers-react';
import DateTimePicker from 'react-datetime-picker';
import { useState, useEffect } from 'react';
import 'react-calendar/dist/Calendar.css';
import 'react-clock/dist/Clock.css';
import 'react-datetime-picker/dist/DateTimePicker.css';

function App() {
  const [start, setStart] = useState(new Date());
  const [end, setEnd] = useState(new Date());
  const [eventName, setEventName] = useState("");
  const [eventDescription, setEventDescription] = useState("");
  const [events, setEvents] = useState([]);
  const [isCreating, setIsCreating] = useState(false);

  const session = useSession();
  const supabase = useSupabaseClient();
  const { isLoading } = useSessionContext();

  // Hardcoded events for testing
  const hardcodedEvents = [
    {
      id: 1,
      name: "Meeting with team",
      description: "Discuss project progress",
      start: new Date(2025, 3, 6, 10, 0),
      end: new Date(2025, 3, 6, 11, 0),
    },
    {
      id: 2,
      name: "Doctor's Appointment",
      description: "Regular checkup",
      start: new Date(2025, 3, 7, 15, 30),
      end: new Date(2025, 3, 7, 16, 30),
    },
    {
      id: 3,
      name: "Lunch with friends",
      description: "Catch up over lunch",
      start: new Date(2025, 3, 8, 12, 0),
      end: new Date(2025, 3, 8, 13, 30),
    },
  ];

  useEffect(() => {
    if (session) {
      fetchEvents();
    } else {
      // If no session, use hardcoded events
      setEvents(hardcodedEvents);
    }
  }, [session]);

  const fetchEvents = async () => {
    const { data, error } = await supabase
      .from('events')
      .select('*')
      .eq('user_id', session.user.id);
    if (error) {
      console.error("Error fetching events:", error);
    } else {
      setEvents(data);
    }
  };

  const createEvent = async () => {
    const event = {
      name: eventName,
      description: eventDescription,
      start: start.toISOString(),
      end: end.toISOString(),
      user_id: session.user.id
    };

    const { data, error } = await supabase
      .from('events')
      .insert([event]);

    if (error) {
      alert('Error creating event');
    } else {
      setEvents([...events, data[0]]);
      setIsCreating(false);
      resetForm();
    }
  };

  const deleteEvent = async (id) => {
    const { error } = await supabase
      .from('events')
      .delete()
      .eq('id', id);

    if (error) {
      alert('Error deleting event');
    } else {
      setEvents(events.filter(event => event.id !== id));
    }
  };

  const sortEvents = (sortBy) => {
    const sortedEvents = [...events].sort((a, b) => {
      if (sortBy === 'name') {
        return a.name.localeCompare(b.name);
      } else if (sortBy === 'date') {
        return new Date(a.start) - new Date(b.start);
      }
      return 0;
    });
    setEvents(sortedEvents);
  };

  const resetForm = () => {
    setStart(new Date());
    setEnd(new Date());
    setEventName('');
    setEventDescription('');
  };

  if (isLoading) {
    return <></>;
  }

  return (
    <div className="App">
      <div className="card">
        {session ? (
          <>
            <h2>{session.user.email}</h2>
            <div className="events-section">
              <h3>Upcoming Events</h3>
              <div className="events-buttons">
                <button onClick={() => sortEvents('name')}>Sort by Name</button>
                <button onClick={() => sortEvents('date')}>Sort by Date</button>
                <button onClick={() => setIsCreating(true)}>Create Event</button>
              </div>
              <div className="event-list">
                {events.map(event => (
                  <div key={event.id} className="event-card">
                    <h4>{event.name}</h4>
                    <p>{event.description}</p>
                    <p>{new Date(event.start).toLocaleString()} - {new Date(event.end).toLocaleString()}</p>
                    <button onClick={() => deleteEvent(event.id)}>Delete</button>
                  </div>
                ))}
              </div>
            </div>
            {isCreating && (
              <div className="create-event-form">
                <h3>Create Event</h3>
                <p>Start of your event</p>
                <DateTimePicker onChange={setStart} value={start} />
                <p>End of your event</p>
                <DateTimePicker onChange={setEnd} value={end} />
                <p>Event name</p>
                <input type="text" value={eventName} onChange={(e) => setEventName(e.target.value)} />
                <p>Event Description</p>
                <input type="text" value={eventDescription} onChange={(e) => setEventDescription(e.target.value)} />
                <button onClick={createEvent}>Create</button>
                <button onClick={() => setIsCreating(false)}>Cancel</button>
              </div>
            )}
            <button onClick={() => supabase.auth.signOut()}>Sign Out</button>
          </>
        ) : (
          <div>
            <h2>Welcome!</h2>
            <p>Please sign in with Google to continue</p>
            <button onClick={googleSignIn}>Sign In with Google</button>
          </div>
        )}
      </div>
    </div>
  );

  async function googleSignIn() {
    const { error } = await supabase.auth.signInWithOAuth({
      provider: 'google',
      options: {
        scopes: 'https://www.googleapis.com/auth/calendar',
      },
    });
    if (error) {
      alert('Error logging into Google');
      console.log(error);
    }
  }
}

export default App;
