import { useSession, useSupabaseClient, useSessionContext } from '@supabase/auth-helpers-react';
import DateTimePicker from 'react-datetime-picker';
import { useState, useEffect } from 'react';
import 'react-calendar/dist/Calendar.css';
import 'react-clock/dist/Clock.css';
import 'react-datetime-picker/dist/DateTimePicker.css';

function App() {
  const [start, setStart] = useState(new Date());
  const [end, setEnd] = useState(new Date());
  const [eventName, setEventName] = useState("");
  const [attendee, setAttendee] = useState("");
  const [eventDescription, setEventDescription] = useState("");
  const [events, setEvents] = useState([]);
  const [isCreating, setIsCreating] = useState(false);
  const [editingEvent, setEditingEvent] = useState(null);

  const session = useSession();
  const supabase = useSupabaseClient();
  const { isLoading } = useSessionContext();

  const [attendeeEmails, setAttendeeEmails] = useState("");  // State pentru participantii prin email


  const [isUserCreated, setIsUserCreated] = useState(false);
  useEffect(() => {
    // Verificăm dacă utilizatorul este autentificat și nu a fost creat deja
    const createUserIfNeeded = async () => {
      if (session && !isUserCreated) {
        const response = await fetch('http://127.0.0.1:5000/create_user', {
          method: 'POST',
          headers: {
            'Content-Type': 'application/json',
          },
          body: JSON.stringify({ email: session.user.email }),  // Trimitem email-ul utilizatorului
        });

        const data = await response.json();

        if (response.ok) {
          if (data.message === 'User created successfully!') {
            console.log('User created successfully!');
            setIsUserCreated(true); // Setăm că utilizatorul a fost creat
          } else if (data.message === 'User already exists') {
            console.log('User already exists!');
          }
        } else {
          console.error('Failed to create user on backend', data);
        }
      }
    };

    createUserIfNeeded();
