  const createEvent = async () => {
    const event = {
      title: eventName,
      description: eventDescription,
      start_time: start.toISOString(),
      end_time: end.toISOString(),
      organizer_id: session.user.id,  // Folosim id-ul utilizatorului pentru organizer
      attendees: attendeeEmails.split(',').map(email => email.trim())  // Listă de emailuri pentru participanți
    };

    try {
      // Apelul către backend pentru a crea evenimentul
      const response = await fetch('http://127.0.0.1:5000/events', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(event),
      });

      const data = await response.json();

      if (response.ok) {
        setEvents([...events, data.event]);  // Adaugă evenimentul la lista de evenimente
        setIsCreating(false);
        resetForm();
      } else {
        console.error('Error creating event:', data);
        alert('Error creating event');
      }
    } catch (error) {
      console.error('Error:', error);
      alert('Error creating event');
    }
  };


  const deleteEvent = async (id) => {
    const { error } = await supabase
      .from('events')
      .delete()
      .eq('id', id);

    if (error) {
      alert('Error deleting event');
    } else {
      setEvents(events.filter(event => event.id !== id));
    }
  };

  const editEvent = (event) => {
    setEditingEvent(event);
    setStart(new Date(event.start));
    setEnd(new Date(event.end));
    setEventName(event.name);
    setEventDescription(event.description);
    setIsCreating(true); // Switch to edit mode
  };

  const updateEvent = async () => {
    const updatedEvent = {
      id: editingEvent.id,
      name: eventName,
      description: eventDescription,
      start: start.toISOString(),
      end: end.toISOString(),
      user_id: session.user.id
    };

    const { data, error } = await supabase
      .from('events')
      .update(updatedEvent)
      .eq('id', editingEvent.id);

    if (error) {
      alert('Error updating event');
    } else {
      setEvents(events.map((event) =>
        event.id === editingEvent.id ? data[0] : event
      ));
      setEditingEvent(null);
      setIsCreating(false);
      resetForm();
    }
  };

  const sortEvents = (sortBy) => {
    const sortedEvents = [...events].sort((a, b) => {
      if (sortBy === 'name') {
        return a.name.localeCompare(b.name);
      } else if (sortBy === 'date') {
        return new Date(a.start) - new Date(b.start);
      }
      return 0;
    });
    setEvents(sortedEvents);
  };

  const resetForm = () => {
    setStart(new Date());
    setEnd(new Date());
    setEventName('');
    setEventDescription('');
  };

  if (isLoading) {
    return <></>;
  }

  return (
    <div className="App">
      <div className="card">
        {session ? (
          <>
            <h2>{session.user.email}</h2>
            <div className="events-section">
              <h3>Upcoming Events</h3>
              <div className="events-buttons">
                <button onClick={() => sortEvents('name')}>Sort by Name</button>
                <button onClick={() => sortEvents('date')}>Sort by Date</button>
                <button onClick={() => setIsCreating(true)}>Create Event</button>
              </div>
              <div className="event-list">
                {events.map(event => (
                  <div key={event.id} className="event-card">
                    <h4>{event.name}</h4>
                    <p>{event.description}</p>
                    <p>{new Date(event.start).toLocaleString()} - {new Date(event.end).toLocaleString()}</p>
                    <button onClick={() => editEvent(event)}>Edit</button>
                    <button onClick={() => deleteEvent(event.id)}>Delete</button>
                  </div>
                ))}
              </div>
            </div>
            {isCreating && (
              <div className="create-event-form">
                <h3>{editingEvent ? "Edit Event" : "Create Event"}</h3>
                <p>Start of your event</p>
                <DateTimePicker onChange={setStart} value={start} />
                <p>End of your event</p>
                <DateTimePicker onChange={setEnd} value={end} />
                <p>Event name</p>
                <input type="text" value={eventName} onChange={(e) => setEventName(e.target.value)} />
                <p>Event Description</p>
                <input type="text" value={eventDescription} onChange={(e) => setEventDescription(e.target.value)} />
                <p>Attendees (separate emails with commas)</p>
                <input 
                  type="text" 
                  value={attendeeEmails} 
                  onChange={(e) => setAttendeeEmails(e.target.value)} 
                />
                <p></p>
                <button onClick={editingEvent ? updateEvent : createEvent}>
                  {editingEvent ? "Update" : "Create"}
                </button>
                <button onClick={() => setIsCreating(false)}>Cancel</button>
              </div>
            )}
            <button onClick={signOut}>Sign Out</button>
          </>
        ) : (
          <div>
            <h2>Welcome!</h2>
            <p>Please sign in with Google to continue</p>
            <button onClick={googleSignIn}>Sign In with Google</button>
          </div>
        )}
      </div>
    </div>
  );

  async function googleSignIn() {
    const { error } = await supabase.auth.signInWithOAuth({
      provider: 'google',
      options: {
        scopes: 'https://www.googleapis.com/auth/calendar',
      },
    });

    if (error) {
      alert('Error logging into Google');
      console.log(error);
    }
  }

  async function signOut() {
    await supabase.auth.signOut();
  }

}

