    alert("Event created in Google Calendar");
    return data.id; // <- ID-ul din Google Calendar
  } else {
    console.error(data);
    throw new Error("Failed to create Google Calendar event");
  }
}


  const createEvent = async () => {
    // Construiește obiectul evenimentului
    const timeZone = Intl.DateTimeFormat().resolvedOptions().timeZone;
    const google_event_id = await createCalendarEvent();
    setGoogleEventId(google_event_id);
    console.log(google_event_id) 

    const event = {
      title: eventName,
      description: eventDescription,
      start_time: start.toLocaleString('en-US', { timeZone }),  // Folosește timeZone pentru start
      end_time: end.toLocaleString('en-US', { timeZone }),      // Asigură-te că end este un obiect Date
      attendees: attendeeEmails.split(',').map(email => email.trim()),  // Listează attendee-urile
      google_event_id : google_event_id
    };

    try {
      // Obține ID-ul organizer-ului pe baza email-ului
      const response = await fetch(`http://127.0.0.1:5000/get_user_id?email=${session.user.email}`);
      const responseData = await response.json();

      if (!response.ok) {
        throw new Error('Failed to fetch organizer ID');
      }

      // Asigură-te că răspunsul conține ID-ul utilizatorului
      if (responseData.id) {
        event.organizer_id = responseData.id;  // Adaugă organizer_id în obiectul evenimentului
      } else {
        throw new Error('User not found');
      }

      // Trimite cererea POST către backend pentru a crea evenimentul
      const createResponse = await fetch('http://127.0.0.1:5000/events', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',  // Indică faptul că trimitem JSON
        },
        body: JSON.stringify(event),  // Transformă obiectul JavaScript într-un JSON string
      });

      const data = await createResponse.json();  // Obține răspunsul ca JSON

      if (createResponse.ok) {
        // Adaugă evenimentul nou la lista de evenimente
        setEvents([...events, { ...event, id: data.event_id }]);
        setIsCreating(false);
        resetForm();

      } else {
        console.error('Error creating event:', data);
        alert('Error creating event');
      }
    } catch (error) {
      console.error('Error:', error);
      alert('Error creating event');
    }
  };


  const deleteEvent = async (eventId) => {
    try {
      // Obține ID-ul utilizatorului curent
      const response = await fetch(`http://127.0.0.1:5000/get_user_id?email=${session.user.email}`);
      const data = await response.json();

      if (!response.ok || !data.id) {
        throw new Error("Could not fetch user ID");
      }

      // Apelează backend-ul pentru a șterge evenimentul
      const deleteResponse = await fetch(`http://127.0.0.1:5000/events/${eventId}?organizer_id=${data.id}`, {
        method: 'DELETE',
      });


      if (deleteResponse.ok) {
        // Șterge evenimentul local din listă
        const eventToDelete = events.find(event => event.id === eventId);
        deleteCalendarEvent(eventToDelete.google_event_id)

        setEvents(events.filter((event) => event.id !== eventId));
      } else {
        const errorData = await deleteResponse.json();
        alert(`Error deleting event: ${errorData.error || 'Unknown error'}`);
      }
    } catch (err) {
      console.error("Delete failed:", err);
      alert("Failed to delete event");
    }
  };

